func InterfaceToDomainSlice(data interface{}) []*Domain {
	list := data.([]interface{})
	result := MakeDomainSlice()
	for _, item := range list {
		result = append(result, InterfaceToDomain(item))
	}
	return result
}

func MakeDomainSlice() []*Domain {
	return []*Domain{}
}
