func InterfaceToVirtualIPSlice(data interface{}) []*VirtualIP {
	list := data.([]interface{})
	result := MakeVirtualIPSlice()
	for _, item := range list {
		result = append(result, InterfaceToVirtualIP(item))
	}
	return result
}

func MakeVirtualIPSlice() []*VirtualIP {
	return []*VirtualIP{}
}
